mapper.update:
update


		goods_db set status=1 where gds_no=${acceptNo}


		funding_db set status=1 where fund_no=${acceptNo}


		class_db set status=1 where cls_no=${acceptNo}

update


		goods_db set status=3 where gds_no=${acceptNo}


		funding_db set status=3 where fund_no=${acceptNo}


		class_db set status=3 where cls_no=${acceptNo}

mapper.select:
select * from (select rownum as rnum, g.* from ( select gds_no as acceptNo, gds_name as acceptName, gds_pri as acceptPrice, goods_date as acceptDate, sel_name as seller, status from goods_db join seller_db using (sel_no) where gds_name like '%'||#{search}||'%' and status=0 and goods_date
= #{endDate}
between to_date(#{startDate}) and to_date(#{endDate})
)g) where rnum between #{start} and #{end}

select * from (select rownum as rnum, f.* from ( select fund_no as acceptNo, fund_name as acceptName, fund_det as acceptDet, fund_money as acceptPrice, fund_date as acceptDate, sel_name as seller, status from funding_db join seller_db using (sel_no) where fund_name like '%'||#{search}||'%' and status=0 and fund_date
= #{endDate}
between to_date(#{startDate}) and to_date(#{endDate})
)f) where rnum between #{start} and #{end}

select * from (select rownum as rnum, c.* from ( select cls_no as acceptNo, cls_name as acceptName, cls_det as acceptDet, cls_price as acceptPrice, class_date as acceptDate, sel_name as seller, status from class_db join seller_db using (sel_no) where cls_name like '%'||#{search}||'%' and status=0 and class_date
= #{endDate}
between to_date(#{startDate}) and to_date(#{endDate})
)c) where rnum between #{start} and #{end}

mapper.delete:
delete from


		client_db where cli_no = #{userNo}


		seller_db where seller_no = #{userNo}

mapper.select:
select * from (select rownum as rnum, c.* from ( select cli_no as userNo, cli_id as userId, cli_name as userName, cli_tel as userPhone, cli_addr as userAddr, cli_email as userEmail, cli_rank as status, cli_enroll as userEnroll from client_db where
cli_id like '%'||#{search}||'%'
cli_name like '%'||#{search}||'%'
and cli_enroll = #{startDate}
and cli_enroll between to_date(#{startDate}) and to_date(#{endDate})
)c) where rnum between #{start} and #{end}
